const vpsMetrics = JSON.parse($input.first().json.stdout);
const websiteStatus = JSON.parse($input.all()[1].json.stdout);
const dockerStatus = JSON.parse($input.all()[2].json.stdout);

const memoryPercent = parseFloat(vpsMetrics.memory_percent);
const cpuUsage = parseFloat(vpsMetrics.cpu_usage);
const diskPercent = parseFloat(vpsMetrics.disk_percent);

const healthStatus = (
  memoryPercent < 80 && cpuUsage < 80 && diskPercent < 80 
  ? 'üü¢ Healthy' 
  : memoryPercent > 90 || cpuUsage > 90 || diskPercent > 90
  ? 'üî¥ Critical'
  : 'üü° Warning'
);

const websiteList = Object.entries(websiteStatus).map(([name, data]) => {
  const status = data.status === '200' ? '‚úÖ' : '‚ùå';
  const responseTime = (parseFloat(data.response_time) * 1000).toFixed(0);
  return `${status} ${name}: ${data.status} (${responseTime}ms)`;
}).join('\n');

const dockerList = Object.entries(dockerStatus).map(([name, status]) => {
  const icon = status === 'Up' ? '‚úÖ' : '‚ùå';
  return `${icon} ${name}: ${status}`;
}).join('\n');

const htmlBody = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 800px; margin: 0 auto; padding: 20px; }
    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; border-radius: 10px; margin-bottom: 30px; }
    .section { background: #f8f9fa; padding: 20px; border-radius: 8px; margin-bottom: 20px; }
    .metric { display: inline-block; margin: 10px 20px 10px 0; }
    .metric-label { font-size: 12px; color: #666; text-transform: uppercase; }
    .metric-value { font-size: 24px; font-weight: bold; color: #667eea; }
    .status-good { color: #28a745; }
    .status-warning { color: #ffc107; }
    .status-critical { color: #dc3545; }
    h2 { color: #667eea; margin-top: 0; }
    pre { background: white; padding: 15px; border-radius: 5px; overflow-x: auto; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>üñ•Ô∏è Daily VPS Health Report</h1>
      <p>${vpsMetrics.timestamp}</p>
      <p><strong>Overall Status:</strong> ${healthStatus}</p>
    </div>

    <div class="section">
      <h2>üìä System Resources</h2>
      <div class="metric">
        <div class="metric-label">CPU Usage</div>
        <div class="metric-value">${cpuUsage.toFixed(1)}%</div>
      </div>
      <div class="metric">
        <div class="metric-label">Memory</div>
        <div class="metric-value">${memoryPercent.toFixed(1)}%</div>
        <div class="metric-label">${vpsMetrics.memory_used}MB / ${vpsMetrics.memory_total}MB</div>
      </div>
      <div class="metric">
        <div class="metric-label">Disk</div>
        <div class="metric-value">${diskPercent.toFixed(0)}%</div>
        <div class="metric-label">${vpsMetrics.disk_used} / ${vpsMetrics.disk_total}</div>
      </div>
      <div class="metric">
        <div class="metric-label">Uptime</div>
        <div class="metric-value">${vpsMetrics.uptime}</div>
      </div>
      <div class="metric">
        <div class="metric-label">Load Average</div>
        <div class="metric-value">${vpsMetrics.load_average}</div>
      </div>
    </div>

    <div class="section">
      <h2>üåê Website Status</h2>
      <pre>${websiteList}</pre>
    </div>

    <div class="section">
      <h2>üê≥ Docker Containers</h2>
      <pre>${dockerList}</pre>
    </div>

    <div class="section">
      <h2>üìù Log Files</h2>
      <p>Service Monitor Log: /var/log/service-monitor.log</p>
      <p>Last check: See cron logs for details</p>
    </div>
  </div>
</body>
</html>
`;

return {
  subject: `VPS Health Report - ${healthStatus} - ${vpsMetrics.timestamp.split('T')[0]}`,
  html: htmlBody,
  metrics: vpsMetrics,
  websites: websiteStatus,
  docker: dockerStatus
};
